import React, {useState} from 'react';
import TopScreen from "./Components/TopScreen/TopScreen";
import Frame from "./Components/Frame/Frame";
import '@vkontakte/vkui/dist/fonts.css';
import {
    AdaptivityProvider, Alert,
    AppRoot, Cell, CellButton,
    ConfigProvider, Epic, Group, ModalPage, ModalPageHeader, ModalRoot, Panel, PanelHeader,
    Placeholder, Separator, Snackbar,
    SplitCol,
    SplitLayout, Tabbar, TabbarItem, useAdaptivity, usePlatform, View,
    ViewWidth, VKCOM, ANDROID, IOS, PanelHeaderBack,
} from "@vkontakte/vkui";

import {Icon28HomeOutline, Icon28TagOutline,Icon28PawOutline } from "@vkontakte/icons";
import ReactDOM from "react-dom";
import HeadPanel from "./Components/TopScreen/PanelHeader/HeadPanel";

const App = (props) => {

    // const viewWidth = useAdaptivity().viewWidth;
    let viewWidth = 5;
    console.log(viewWidth)

    const platform = usePlatform();
    console.log(platform)

    const [activeStory, setActiveStory] = React.useState("Shop");
    const onStoryChange = (e) => {
        setActiveStory(e.currentTarget.dataset.story);
        setSimple(e.currentTarget.dataset.story)
    }
    const isDesktop = viewWidth >= ViewWidth.SMALL_TABLET;
    const hasHeader = platform !== VKCOM;


    const [simple, setSimple] = useState("Shop");
    // попап
    const [popout, setPopout] = React.useState(null);
    const closePopout = () => {
        setPopout(null);
    };
    function limitExceeded (){
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Закрыть",
                        autoclose: true,
                        mode: "destructive",
                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                // header="Лимит покупки превышен"
                header="Лимит покупки превышен"
            />
        );
    };

    // Модальное окно
    const [modal, setModal] = React.useState(null);
    const modalRoot = (
        <ModalRoot activeModal={modal}>
            <ModalPage
                id="warning"
                onClose={() => setModal(null)}
                header={<ModalPageHeader>По нулям...</ModalPageHeader>}
            >
                <Group>
                    <CellButton onClick={() =>reloadVk()}>Сброс приложения</CellButton>
                </Group>
            </ModalPage>
        </ModalRoot>
    );
    const reloadVk = () => {
        setModal(null)
        location.reload ()
    }
    // функция вызова содального окна
    const modalRootFunc = () => {
        setModal("warning")
    }

    // Всплывающее окно
    const [snackbar, setSnackbar] = React.useState(null);
    const openAction = () => {
        if (snackbar) return;
        setSnackbar(
            <Snackbar
                layout="vertical"
                onClose={() => setSnackbar(null)}
            >
                Слишком высокая активность
            </Snackbar>
        );
    };

    return (
        <ConfigProvider platform="vkcom">
            <AdaptivityProvider>
                <AppRoot >
                    <SplitLayout
                        popout={popout}
                        modal={modalRoot}
                        style={{ justifyContent: "center" }}
                        header={hasHeader && <PanelHeader separator={false} />}
                    >
                        {viewWidth >= ViewWidth.DESKTOP && (
                            <SplitCol fixed width={220} maxWidth={220} style={{marginRight: '25px'}}>
                                <Panel >
                                    {hasHeader && <PanelHeader />}
                                    <Group>
                                        <Cell
                                            disabled={activeStory === "Home"}
                                            style={
                                                activeStory === "Home"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="Home"
                                            onClick={onStoryChange}
                                            before={
                                                <TabbarItem
                                                    style={{marginRight: "10px"}}
                                                    selected={activeStory && simple  ===  "Home"}
                                                    data-story="Home"
                                                >
                                                    <Icon28HomeOutline />
                                                </TabbarItem>
                                            }
                                        >
                                            Моя страница
                                        </Cell>

                                        <Cell
                                            disabled={activeStory === "messages"}
                                            style={
                                                activeStory === "messages"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="messages"
                                            onClick={onStoryChange}
                                            before={
                                                <TabbarItem
                                                    style={{marginRight: "10px"}}
                                                    selected={activeStory && simple  ===  "messages"}
                                                    data-story="messages"
                                                >
                                                    <Icon28PawOutline />
                                                </TabbarItem>
                                            }
                                        >
                                            Сообщения
                                        </Cell>

                                        <Cell
                                            disabled={activeStory === "Shop"}
                                            style={
                                                activeStory === "Shop"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="Shop"
                                            onClick={onStoryChange}
                                            before={
                                                <TabbarItem
                                                    style={{marginRight: "10px"}}
                                                    selected={activeStory && simple  ===  "Shop"}
                                                    data-story="Shop"
                                                >
                                                    <Icon28TagOutline />
                                                </TabbarItem>
                                            }
                                        >
                                            Магазин
                                        </Cell>
                                    </Group>
                                </Panel>
                            </SplitCol>
                        )}

                        <SplitCol
                            animate={!isDesktop}
                            spaced={isDesktop}
                            width={isDesktop ? "560px" : "100%"}
                            maxWidth={isDesktop ? "560px" : "100%"}
                        >
                            <Epic style={{margin: '-10px 0 0 -3px'}}
                                activeStory={activeStory}
                                tabbar={
                                    !isDesktop && (
                                        <Tabbar >
                                            <TabbarItem
                                                onClick={onStoryChange}
                                                selected={activeStory && simple  ===  "Home"}
                                                data-story="Home"
                                            >
                                                <Icon28HomeOutline/>
                                            </TabbarItem>

                                            <TabbarItem
                                                onClick={onStoryChange}
                                                selected={activeStory && simple  === "messages"}
                                                data-story="messages"
                                            >
                                                <Icon28PawOutline/>
                                            </TabbarItem>

                                            <TabbarItem
                                                onClick={onStoryChange}
                                                selected={activeStory && simple  === "Shop"}
                                                data-story="Shop"
                                            >
                                                <Icon28TagOutline />
                                            </TabbarItem>
                                        </Tabbar>
                                    )
                                }
                            >
                                <View id="Home" activePanel="Home">
                                    <Panel id="Home">
                                        <PanelHeader>Home</PanelHeader>
                                        <Group style={{ height: "400px" }}>
                                            <Placeholder>
                                                <Icon28HomeOutline/>
                                            </Placeholder>
                                        </Group>
                                    </Panel>
                                </View>

                                <View id="messages" activePanel="messages">
                                    <Panel id="messages">
                                        <PanelHeader>messages</PanelHeader>
                                        <Group style={{ height: "400px" }}>
                                            <Placeholder>
                                                <Icon28PawOutline/>
                                            </Placeholder>
                                        </Group>
                                    </Panel>
                                </View>

                                <View id="Shop" activePanel="Shop">
                                    <Panel id="Shop" onmousedown="return false" onselectstart="return false">
                                        <Group >
                                            <TopScreen />
                                            <Separator />
                                            <Frame
                                                limitExceeded={limitExceeded}
                                                modalRootFunc={modalRootFunc}
                                                openAction={openAction}
                                            />
                                            {snackbar}
                                        </Group>
                                    </Panel>
                                </View>
                            </Epic>
                        </SplitCol>
                    </SplitLayout>
                </AppRoot>
            </AdaptivityProvider>
        </ConfigProvider>

    );
}
export default App;
