import React from "react";
import style from "./HeadPanel.module.css";
import coins from "./IconHeader/coins.svg";
import {useSelector} from "react-redux";
import {Alert, ModalPage, ModalPageHeader, ModalRoot, Panel, View} from "@vkontakte/vkui";

const HeadPanel = () => {
    const [popout, setPopout] = React.useState(null);

    let bankCoins = useSelector(state => state.bankCoins.bankCoinNum)


    const closePopout = () => {
        setPopout(null);
    };

    const openDeletion = () => {
        setPopout(
            <Alert
                actions={[

                    {
                        title: "Удалить",
                        autoclose: true,
                        mode: "destructive",

                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                header="Удаление документа"
                text="Вы уверены, что хотите удалить этот документ?"
            />
        );
    };
    if (bankCoins <= 95000) {
        openDeletion()
    } else if (bankCoins <= 0) {
        alert("сброс приложения")
    }
    if (bankCoins >= 1000) {
        bankCoins = Math.trunc(bankCoins / 1000) + 'k';
    }
    return (
        <div className={style.panel_header} activePanel="alert" popout={popout}>
            <div className={style.panel_header_content} id="alert">
                <div className={style.button}>
                    <div className={style.coins_block}>
                        <img src={coins} alt="coins"/>
                    </div>
                    <div className={style.sum_coins}>{bankCoins}</div>
                </div>
                <div className={style.text_center}>Магазин</div>
            </div>
        </div>

    );
}

export default HeadPanel;

