import React from 'react';

import '@vkontakte/vkui/dist/fonts.css';
import {
    Cell,
    Group,
    Panel,
    PanelHeader, SplitCol, TabbarItem, usePlatform, VKCOM

} from "@vkontakte/vkui";
import {Icon28HomeOutline, Icon28TagOutline,Icon28PawOutline } from "@vkontakte/icons";



const DesktopMenu = () => {
    const platform = usePlatform();
    const hasHeader = platform !== VKCOM;
    const [activeStory, setActiveStory] = React.useState("Shop");
    const onStoryChange = (e) => {
        setActiveStory(e.currentTarget.dataset.story);
        setSimple(e.currentTarget.dataset.story)
    }
    return (
        <SplitCol fixed width={200} maxWidth={200} >
            <Panel >
                {hasHeader && <PanelHeader />}
                <Group>
                    <Cell
                        disabled={activeStory === "Home"}
                        style={
                            activeStory === "Home"
                                ? {
                                    backgroundColor:
                                        "var(--vkui--color_background_secondary)",
                                    borderRadius: 8,
                                }
                                : {}
                        }
                        data-story="Home"
                        onClick={onStoryChange}
                        before={
                            <TabbarItem
                                style={{marginRight: "10px"}}
                                selected={activeStory && simple  ===  "Home"}
                                data-story="Home"
                            >
                                <Icon28HomeOutline />
                            </TabbarItem>
                        }
                    >
                        Home
                    </Cell>

                    <Cell
                        disabled={activeStory === "Paw"}
                        style={
                            activeStory === "Paw"
                                ? {
                                    backgroundColor:
                                        "var(--vkui--color_background_secondary)",
                                    borderRadius: 8,
                                }
                                : {}
                        }
                        data-story="Paw"
                        onClick={onStoryChange}
                        before={
                            <TabbarItem
                                style={{marginRight: "10px"}}
                                selected={activeStory && simple  ===  "Paw"}
                                data-story="Paw"
                            >
                                <Icon28PawOutline />
                            </TabbarItem>
                        }
                    >
                        Paw
                    </Cell>

                    <Cell
                        disabled={activeStory === "Shop"}
                        style={
                            activeStory === "Shop"
                                ? {
                                    backgroundColor:
                                        "var(--vkui--color_background_secondary)",
                                    borderRadius: 8,
                                }
                                : {}
                        }
                        data-story="Shop"
                        onClick={onStoryChange}
                        before={
                            <TabbarItem
                                style={{marginRight: "10px"}}
                                selected={activeStory && simple  ===  "Shop"}
                                data-story="Shop"
                            >
                                <Icon28TagOutline />
                            </TabbarItem>
                        }
                    >
                        Shop
                    </Cell>
                </Group>
            </Panel>
        </SplitCol>

    );
}

export default DesktopMenu;
