import React from 'react';
import TopScreen from "./Components/TopScreen/TopScreen";
import Frame from "./Components/Frame/Frame";
import '@vkontakte/vkui/dist/fonts.css';
import {
    AdaptivityProvider, Alert,
    AppRoot, Cell, CellButton,
    ConfigProvider, Epic, Group, ModalPage, ModalPageHeader, ModalRoot, Panel,
    Placeholder, Separator,
    SplitCol,
    SplitLayout, Tabbar, TabbarItem, useAdaptivity, usePlatform, View,
    ViewWidth, VKCOM,
} from "@vkontakte/vkui";

import icon_1 from "./img1.svg";
import icon_2 from "./img2.svg";
import icon_3 from "./img3.svg";


const App = () => {

    const viewWidth = useAdaptivity().viewWidth;
    // let viewWidth = 5;
    console.log(viewWidth)

    const platform = usePlatform();
    // const platform = VKCOM;
    console.log(platform)

    const [activeStory, setActiveStory] = React.useState("Shop");
    const onStoryChange = (e) => setActiveStory(e.currentTarget.dataset.story);
    const isDesktop = viewWidth >= ViewWidth.SMALL_TABLET;
    const hasHeader = platform !== VKCOM;

    const [popout, setPopout] = React.useState(null);

    const closePopout = () => {
        setPopout(null);
    };
    function limitExceeded (){
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Закрыть",
                        autoclose: true,
                        mode: "destructive",
                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                header="Лимит покупки превышен"
            />
        );
    };

    const [modal, setModal] = React.useState(null);

    const modalRoot = (
        <ModalRoot activeModal={modal}>
            <ModalPage
                id="warning"
                onClose={() => setModal(null)}
                header={<ModalPageHeader>Modal 1</ModalPageHeader>}
            >
                <Group>
                    <CellButton onClick={() => setModal("warning")}>Modal 2</CellButton>
                </Group>
            </ModalPage>
        </ModalRoot>
    );

    const modalRootFunc = () => setModal("warning")

    return (
        <ConfigProvider >
            <AdaptivityProvider>
                <AppRoot >
                    <SplitLayout popout={popout} modal={modalRoot}>
                        {viewWidth >= ViewWidth.DESKTOP && (
                            <SplitCol fixed width={220} maxWidth={220} style={{marginRight: '25px'}}>
                                <Panel >
                                    {hasHeader}
                                    {/*{hasHeader && <PanelHeader />}*/}
                                    <Group>
                                        <Cell
                                            disabled={activeStory === "Home"}
                                            style={
                                                activeStory === "Home"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="Home"
                                            onClick={onStoryChange}

                                            after={<img src={icon_1}/>}
                                        >
                                            Home
                                        </Cell>

                                        <Cell
                                            disabled={activeStory === "messages"}
                                            style={
                                                activeStory === "messages"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="messages"
                                            onClick={onStoryChange}
                                            after={<img src={icon_2}/>}
                                        >
                                            Messages
                                        </Cell>

                                        <Cell
                                            disabled={activeStory === "Shop"}
                                            style={
                                                activeStory === "Shop"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="Shop"
                                            onClick={onStoryChange}
                                            after={<img src={icon_3}/>}
                                        >
                                            Shop
                                        </Cell>
                                    </Group>
                                </Panel>
                            </SplitCol>
                        )}

                        <SplitCol
                            animate={!isDesktop}
                            spaced={isDesktop}
                            width={isDesktop ? "560px" : "100%"}
                            maxWidth={isDesktop ? "560px" : "100%"}
                        >
                            <Epic
                                activeStory={activeStory}
                                tabbar={
                                    !isDesktop && (
                                        <Tabbar>
                                            <TabbarItem
                                                onClick={onStoryChange}
                                                selected={activeStory === "Home"}
                                                data-story="Home"
                                            >
                                                <img src={icon_1}/>
                                            </TabbarItem>

                                            <TabbarItem
                                                onClick={onStoryChange}
                                                selected={activeStory === "messages"}
                                                data-story="messages"
                                            >
                                                <img src={icon_2}/>
                                            </TabbarItem>

                                            <TabbarItem
                                                onClick={onStoryChange}
                                                selected={activeStory === "Shop"}
                                                data-story="Shop"
                                            >
                                                <img src={icon_3}/>
                                            </TabbarItem>
                                        </Tabbar>
                                    )
                                }
                            >
                                <View id="Home" activePanel="Home">
                                    <Panel id="Home">
                                        <Group style={{ height: "1000px" }}>
                                            <Placeholder
                                                icon={<img src={icon_1}/>}
                                            />
                                        </Group>
                                    </Panel>
                                </View>

                                <View id="messages" activePanel="messages">
                                    <Panel id="messages">
                                        <Group style={{ height: "1000px" }}>
                                            <Placeholder
                                                icon={<img src={icon_2}/>}
                                            ></Placeholder>
                                        </Group>
                                    </Panel>
                                </View>

                                <View id="Shop" activePanel="Shop">
                                    <Panel id="Shop" onmousedown="return false" onselectstart="return false">
                                        <Group style={{margin: '-10px 0 0 -3px'}}>
                                            <TopScreen />
                                            <Separator />
                                            <Frame limitExceeded={limitExceeded} modalRootFunc={modalRootFunc}/>
                                        </Group>
                                    </Panel>
                                </View>
                            </Epic>
                        </SplitCol>
                    </SplitLayout>
                </AppRoot>
            </AdaptivityProvider>
        </ConfigProvider>

    );
}
export default App;







