import React from 'react';
import {
    Badge,
    Cell, Counter, Epic,
    Group,
    Panel,
    PanelHeader, PanelHeaderBack, Placeholder,
    SplitCol,
    SplitLayout, Tabbar, TabbarItem, usePlatform, View,
    ViewWidth,
    VKCOM,
    withAdaptivity
} from "@vkontakte/vkui";
import {
    Icon28ClipOutline,
    Icon28MessageOutline,
    Icon28NewsfeedOutline, Icon28ServicesOutline, Icon28UserCircleOutline, Icon56NewsfeedOutline,
} from "@vkontakte/icons";
import icon1 from "../Footer/img/home_outline_28.svg";
import icon2 from "../Footer/img/Icon222.svg";
import icon3 from "../Footer/img/Icon333.svg";
import Frame from "../Frame/Frame";
import TopScreen from "../TopScreen/TopScreen";
import Example from "./Components/EpicBlock/EpicBlock";


const Example_2 = withAdaptivity(({ viewWidth }) => {
        const platform = usePlatform();
        const [activeStory, setActiveStory] = React.useState("profile");
        const onStoryChange = (e) => setActiveStory(e.currentTarget.dataset.story);
        const isDesktop = viewWidth >= ViewWidth.TABLET;
        const hasHeader = platform !== VKCOM;

        return (
            <SplitLayout
                header={hasHeader && <PanelHeader separator={false} />}
                style={{ justifyContent: "center" }}
            >
                {isDesktop && (
                    <SplitCol fixed width={280} maxWidth={280}>
                        <Panel>
                            {hasHeader && <PanelHeader />}
                            <Group>
                                <Cell
                                    disabled={activeStory === "feed"}
                                    style={
                                        activeStory === "feed"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="feed"
                                    onClick={onStoryChange}
                                    before={<Icon28NewsfeedOutline />}
                                >
                                    feed
                                </Cell>
                                <Cell
                                    disabled={activeStory === "services"}
                                    style={
                                        activeStory === "services"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="services"
                                    onClick={onStoryChange}
                                    before={<Icon28ServicesOutline />}
                                >
                                    services
                                </Cell>
                                <Cell
                                    disabled={activeStory === "messages"}
                                    style={
                                        activeStory === "messages"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="messages"
                                    onClick={onStoryChange}
                                    before={<Icon28MessageOutline />}
                                >
                                    messages
                                </Cell>
                                <Cell
                                    disabled={activeStory === "clips"}
                                    style={
                                        activeStory === "clips"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="clips"
                                    onClick={onStoryChange}
                                    before={<Icon28ClipOutline />}
                                >
                                    clips
                                </Cell>
                                <Cell
                                    disabled={activeStory === "profile"}
                                    style={
                                        activeStory === "profile"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="profile"
                                    onClick={onStoryChange}
                                    before={<Icon28UserCircleOutline />}
                                >
                                    profile
                                </Cell>
                            </Group>
                        </Panel>
                    </SplitCol>
                )}

                <SplitCol
                    animate={!isDesktop}
                    spaced={isDesktop}
                    width={isDesktop ? "560px" : "560px"}
                    maxWidth={isDesktop ? "560px" : "560px"}
                >
                    <Epic
                        activeStory={activeStory}
                        tabbar={
                            !isDesktop && (
                                <Tabbar>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "feed"}
                                        data-story="feed"
                                        text="Новости"
                                    >
                                        <Icon28NewsfeedOutline />
                                    </TabbarItem>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "services"}
                                        data-story="services"
                                        text="Сервисы"
                                    >
                                        <Icon28ServicesOutline />
                                    </TabbarItem>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "messages"}
                                        data-story="messages"
                                        indicator={
                                            <Counter size="s" mode="prominent">
                                                12
                                            </Counter>
                                        }
                                        text="Сообщения"
                                    >
                                        <Icon28MessageOutline />
                                    </TabbarItem>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "clips"}
                                        data-story="clips"
                                        text="Клипы"
                                    >
                                        <Icon28ClipOutline />
                                    </TabbarItem>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "profile"}
                                        data-story="profile"
                                        indicator={<Badge mode="prominent" />}
                                        text="Профиль"
                                    >
                                        <Icon28UserCircleOutline />
                                    </TabbarItem>
                                </Tabbar>
                            )
                        }
                    >
                        <View id="feed" activePanel="feed">
                            <Panel id="feed">
                                <PanelHeader before={<PanelHeaderBack />}>Новости</PanelHeader>
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder
                                        icon={<Icon56NewsfeedOutline width={56} height={56} />}
                                    />
                                </Group>
                            </Panel>
                        </View>
                        <View id="services" activePanel="services">
                            <Panel id="services">
                                <PanelHeader before={<PanelHeaderBack />}>Сервисы</PanelHeader>
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder
                                        icon={<Icon28ServicesOutline width={56} height={56} />}
                                    ></Placeholder>
                                </Group>
                            </Panel>
                        </View>
                        <View id="messages" activePanel="messages">
                            <Panel id="messages">
                                <PanelHeader before={<PanelHeaderBack />}>
                                    Сообщения
                                </PanelHeader>
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder
                                        icon={<Icon28MessageOutline width={56} height={56} />}
                                    ></Placeholder>
                                </Group>
                            </Panel>
                        </View>
                        <View id="clips" activePanel="clips">
                            <Panel id="clips">
                                <PanelHeader before={<PanelHeaderBack />}>Клипы</PanelHeader>
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder
