import React from "react";
import style from "./HeadPanel.module.css";
import coins from "./IconHeader/coins.svg";
import {useSelector} from "react-redux";
const HeadPanel = () => {

    let bankCoins = useSelector(state => state.bankCoins.bankCoinNum)
    if (bankCoins <= 95000) {
        alert('s')
    } else if (bankCoins <= 0) {
        alert("сброс приложения")
    }
    if (bankCoins >= 1000) {
        bankCoins = Math.trunc(bankCoins / 1000) + 'k';
    }

    const [modal, setModal] = React.useState(null);

    const modalRoot = (
        <ModalRoot activeModal={modal}>
            <ModalPage
                id={modals[0]}
                onClose={() => setModal(null)}
                header={<ModalPageHeader>Modal 1</ModalPageHeader>}
            >
                <Group>
                    <CellButton onClick={() => setModal(modals[1])}>Modal 2</CellButton>
                </Group>
            </ModalPage>
            <ModalPage
                id={modals[1]}
                onClose={() => setModal(null)}
                header={<ModalPageHeader>Modal 2</ModalPageHeader>}
            >
                <Group>
                    <CellButton onClick={() => setModal(modals[0])}>Modal 1</CellButton>
                </Group>
            </ModalPage>
        </ModalRoot>
    );

    return (
        <div className={style.panel_header}>
            <div className={style.panel_header_content}>
                <div className={style.button}>
                    <div className={style.coins_block}>
                        <img src={coins} alt="coins"/>
                    </div>
                    <div className={style.sum_coins}>{bankCoins}</div>
                </div>
                <div className={style.text_center}>Магазин</div>
                {/*<div className={style.right_but}>*/}
                {/*    <div className={style.right_but_body}>*/}
                {/*        <div className={style.butt_three}>*/}
                {/*            <img src={more}/>*/}
                {/*        </div>*/}
                {/*        <div className={style.separator}></div>*/}
                {/*        <div className={style.butt_close}>*/}
                {/*            <img src={close}/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
            </div>
        </div>

    );
}

export default HeadPanel;
