import React, {useState} from 'react';
import TopScreen from "./Components/TopScreen/TopScreen";
import Frame from "./Components/Frame/Frame";
import '@vkontakte/vkui/dist/fonts.css';
import {
    AdaptivityProvider, Alert,
    AppRoot, Cell, CellButton,
    ConfigProvider, Epic, Group, ModalPage, ModalPageHeader, ModalRoot, Panel, PanelHeader,
    Placeholder, Separator, Snackbar,
    SplitCol,
    SplitLayout, Tabbar, TabbarItem, useAdaptivity, usePlatform, View,
    ViewWidth, VKCOM,
} from "@vkontakte/vkui";

import icon_1 from "./img1.svg";
import icon_2 from "./img2.svg";
import icon_3 from "./img3.svg";

const App = (props) => {

    const viewWidth = useAdaptivity().viewWidth;
    // let viewWidth = 5;
    console.log(viewWidth)

    const platform = usePlatform();
    console.log(platform)

    const [activeStory, setActiveStory] = React.useState("Shop");
    const onStoryChange = (e) => setActiveStory(e.currentTarget.dataset.story);
    const isDesktop = viewWidth >= ViewWidth.SMALL_TABLET;
    const hasHeader = platform !== VKCOM;


    const [simple, setSimple] = useState("one");
    // попап
    const [popout, setPopout] = React.useState(null);
    const closePopout = () => {
        setPopout(null);
    };
    function limitExceeded (){
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Закрыть",
                        autoclose: true,
                        mode: "destructive",
                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                // header="Лимит покупки превышен"
                header="Лимит покупки превышен"
            />
        );
    };

    // Модальное окно
    const [modal, setModal] = React.useState(null);
    const modalRoot = (
        <ModalRoot activeModal={modal}>
            <ModalPage
                id="warning"
                onClose={() => setModal(null)}
                header={<ModalPageHeader>По нулям...</ModalPageHeader>}
            >
                <Group>
                    <CellButton onClick={() =>reloadVk()}>Сброс приложения</CellButton>
                </Group>
            </ModalPage>
        </ModalRoot>
    );
    const reloadVk = () => {
        setModal(null)
        location.reload ()
    }
    // функция вызова содального окна
    const modalRootFunc = () => {
        setModal("warning")
    }

    // Всплывающее окно
    const [snackbar, setSnackbar] = React.useState(null);
    const openAction = () => {
        if (snackbar) return;
        setSnackbar(
            <Snackbar
                layout="vertical"
                onClose={() => setSnackbar(null)}
            >
                Слишком высокая активность
            </Snackbar>
        );
    };

    return (
        <ConfigProvider >
            <AdaptivityProvider>
                <AppRoot >
                    <SplitLayout popout={popout} modal={modalRoot}>
                        {viewWidth >= ViewWidth.DESKTOP && (
                            <SplitCol fixed width={220} maxWidth={220} style={{marginRight: '25px'}}>
                                <Panel >
                                    {hasHeader }
                                    <Group>
                                        <Cell
                                            disabled={activeStory === "Home"}
                                            style={
                                                activeStory === "Home"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="Home"
                                            onClick={onStoryChange}

                                            after={<img src={icon_1}  alt="icon"/>}
                                        >
                                            Моя страница
                                        </Cell>

                                        <Cell
                                            disabled={activeStory === "messages"}
                                            style={
                                                activeStory === "messages"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="messages"
                                            onClick={onStoryChange}
                                            after={<img src={icon_2} alt="icon"/>}
                                        >
                                            Сообщения
                                        </Cell>

                                        <Cell
                                            disabled={activeStory === "Shop"}
                                            style={
                                                activeStory === "Shop"
                                                    ? {
                                                        backgroundColor:
                                                            "var(--vkui--color_background_secondary)",
                                                        borderRadius: 8,
                                                    }
                                                    : {}
                                            }
                                            data-story="Shop"
                                            onClick={onStoryChange}
                                            after={<img src={icon_3} alt="icon"/>}
                                        >
                                            Магазин
                                        </Cell>
                                    </Group>
                                </Panel>
                            </SplitCol>
                        )}

                        <SplitCol
                            animate={!isDesktop}
                            spaced={isDesktop}
                            width={isDesktop ? "560px" : "100%"}
                            maxWidth={isDesktop ? "560px" : "100%"}
                        >
                            <Epic style={{margin: '-10px 0 0 -3px'}}
                                activeStory={activeStory}
                                tabbar={
                                    !isDesktop && (
                                        <Tabbar >
                                            <TabbarItem
                                                // onClick={onStoryChange}
                                                onClick={() => setSimple("Home")}
                                                selected={activeStory && indicator ===  "Home"}
                                                data-story="Home"
                                                text='Home'
                                                // selected={indicator === "one"}
                                                // onClick={() => setIndicator("one")}
                                            >
                                                <img src={icon_1} width={28} height={28}/>
                                                {/*<Icon28NewsfeedOutline />*/}
                                            </TabbarItem>

                                            <TabbarItem
                                                // onClick={onStoryChange}
                                                onClick={() => setSimple("messages")}
                                                selected={activeStory && indicator === "messages"}
                                                data-story="messages"
                                                text='messages'

                                            >
                                                <img src={icon_2} width={28} height={28}/>
                                            </TabbarItem>

                                            <TabbarItem
                                                // onClick={onStoryChange}
                                                onClick={() => setSimple("Shop")}
                                                selected={activeStory && indicator === "Shop"}
                                                data-story="Shop"
                                                text='Shop'
                                                // text="Новости"
                                            >
                                                <img src={icon_3} width={28} height={28}/>
                                            </TabbarItem>
                                        </Tabbar>
                                    )
                                }
                            >
                                <View id="Home" activePanel="Home">
                                    <Panel id="Home">
                                        <Group style={{ height: "1000px" }}>
                                            <Placeholder
                                                icon={<img src={icon_1}/>}
                                            />
                                        </Group>
                                    </Panel>
                                </View>

                                <View id="messages" activePanel="messages">
                                    <Panel id="messages">
                                        <Group style={{ height: "1000px" }}>
                                            <Placeholder
                                                icon={<img src={icon_2}/>}
                                            ></Placeholder>
                                        </Group>
                                    </Panel>
                                </View>

                                <View id="Shop" activePanel="Shop">
                                    <Panel id="Shop" onmousedown="return false" onselectstart="return false">
                                        <Group >
                                            <TopScreen />
                                            <Separator />
                                            {snackbar}
                                            <Frame
                                                limitExceeded={limitExceeded}
                                                modalRootFunc={modalRootFunc}
                                                openAction={openAction}
                                            />
                                        </Group>
                                    </Panel>
                                </View>
                            </Epic>
                        </SplitCol>
                    </SplitLayout>
                </AppRoot>
            </AdaptivityProvider>
        </ConfigProvider>

    );
}
export default App;







