import React from 'react';
import {
    Badge,
    Cell, Counter, Epic,
    Group,
    Panel,
    PanelHeader, PanelHeaderBack, Placeholder,
    SplitCol,
    SplitLayout, Tabbar, TabbarItem, usePlatform, View,
    ViewWidth,
    VKCOM,
    withAdaptivity
} from "@vkontakte/vkui";
import {
    Icon28ClipOutline,
    Icon28MessageOutline,
    Icon28NewsfeedOutline, Icon28ServicesOutline, Icon28UserCircleOutline, Icon56NewsfeedOutline,
} from "@vkontakte/icons";

const Example = withAdaptivity(
    ({ viewWidth }) => {
        const platform = usePlatform();
        const [activeStory, setActiveStory] = React.useState("profile");
        const onStoryChange = (e) => setActiveStory(e.currentTarget.dataset.story);
        const isDesktop = viewWidth >= ViewWidth.TABLET;
        const hasHeader = platform !== VKCOM;

        return (
            <SplitLayout
                header={hasHeader && <PanelHeader separator={false} />}
                style={{ justifyContent: "center" }}
            >
                {isDesktop && (
                    <SplitCol fixed width={280} maxWidth={280}>
                        <Panel>
                            {hasHeader && <PanelHeader />}
                            <Group>
                                <Cell
                                    disabled={activeStory === "feed"}
                                    style={
                                        activeStory === "feed"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="feed"
                                    onClick={onStoryChange}
                                    before={<Icon28NewsfeedOutline />}
                                >
                                    feed
                                </Cell>
                                <Cell
                                    disabled={activeStory === "services"}
                                    style={
                                        activeStory === "services"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="services"
                                    onClick={onStoryChange}
                                    before={<Icon28ServicesOutline />}
                                >
                                    services
                                </Cell>
                                <Cell
                                    disabled={activeStory === "messages"}
                                    style={
                                        activeStory === "messages"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="messages"
                                    onClick={onStoryChange}
                                    before={<Icon28MessageOutline />}
                                >
                                    messages
                                </Cell>
                                <Cell
                                    disabled={activeStory === "clips"}
                                    style={
                                        activeStory === "clips"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="clips"
                                    onClick={onStoryChange}
                                    before={<Icon28ClipOutline />}
                                >
                                    clips
                                </Cell>
                                <Cell
                                    disabled={activeStory === "profile"}
                                    style={
                                        activeStory === "profile"
                                            ? {
                                                backgroundColor:
                                                    "var(--vkui--color_background_secondary)",
                                                borderRadius: 8,
                                            }
                                            : {}
                                    }
                                    data-story="profile"
                                    onClick={onStoryChange}
                                    before={<Icon28UserCircleOutline />}
                                >
                                    profile
                                </Cell>
                            </Group>
                        </Panel>
                    </SplitCol>
                )}

                <SplitCol
                    animate={!isDesktop}
                    spaced={isDesktop}
                    width={isDesktop ? "560px" : "100%"}
                    maxWidth={isDesktop ? "560px" : "100%"}
                >
                    <Epic
                        activeStory={activeStory}
                        tabbar={
                            !isDesktop && (
                                <Tabbar>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "messages"}
                                        // data-story="messages"
                                        text="Сообщения"
                                    >
                                        <Icon28MessageOutline />
                                    </TabbarItem>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "clips"}
                                        data-story="clips"
                                        text="Клипы"
                                    >
                                        <Icon28ClipOutline />
                                    </TabbarItem>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "profile"}
                                        data-story="profile"
                                        indicator={<Badge mode="prominent" />}
                                        text="Профиль"
                                    >
                                        <Icon28UserCircleOutline />
                                    </TabbarItem>
                                </Tabbar>
                            )
                        }
                    >

                        <View id="messages" activePanel="messages">
                            <Panel id="messages">
                                <PanelHeader before={<PanelHeaderBack />}>
                                    Сообщения
                                </PanelHeader>
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder
                                        icon={<Icon28MessageOutline width={56} height={56} />}
                                    ></Placeholder>
                                </Group>
                            </Panel>
                        </View>

                        <View id="clips" activePanel="clips">
                            <Panel id="clips">
                                <PanelHeader before={<PanelHeaderBack />}>Клипы</PanelHeader>
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder
                                        icon={<Icon28ClipOutline width={56} height={56} />}
                                    ></Placeholder>
                                </Group>
                            </Panel>
                        </View>

                        <View id="profile" activePanel="profile">
                            <Panel id="profile">
                                <PanelHeader before={<PanelHeaderBack />}>Профиль</PanelHeader>
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder
                                        icon={<Icon28UserCircleOutline width={56} height={56} />}
                                    ></Placeholder>
                                </Group>
                            </Panel>
                        </View>

                    </Epic>
                </SplitCol>
            </SplitLayout>
        );
    },
    {
        viewWidth: true,
    }
);
export default Example;
// <Example />;
