import React from "react";
import style from "./HeadPanel.module.css";
import coins from "./IconHeader/coins.svg";
import {useSelector} from "react-redux";
import {PanelHeaderContent} from "@vkontakte/vkui";

const HeadPanel = () => {
    let bankCoins = useSelector(state => state.bankCoins.bankCoinNum)
    if (bankCoins >= 1000) {
        bankCoins = Math.trunc(bankCoins / 1000) + 'k';
    }
    return (
        <PanelHeaderContent
            before={<Avatar size={36} src={getAvatarUrl("user_va")} />}>
            Магазин
        </PanelHeaderContent>

