import React from "react";
import style from "./HeadPanel.module.css";
import coins from "./IconHeader/coins.svg";
import {useSelector} from "react-redux";
import {ModalPage, ModalPageHeader, ModalRoot} from "@vkontakte/vkui";
const HeadPanel = () => {

    let bankCoins = useSelector(state => state.bankCoins.bankCoinNum)
    if (bankCoins <= 95000) {
        setModal("restarts")
    } else if (bankCoins <= 0) {
        alert("сброс приложения")
    }
    if (bankCoins >= 1000) {
        bankCoins = Math.trunc(bankCoins / 1000) + 'k';
    }

    const [modal, setModal] = React.useState(null);

    const modalRoot = (
        <ModalRoot activeModal={modal}>
            <ModalPage
                id="restarts"
                onClose={() => setModal(null)}
                header={<ModalPageHeader>Обнулить приложение</ModalPageHeader>}
            >
            </ModalPage>
        </ModalRoot>
    );

    return (
        <div className={style.panel_header}>
            <div className={style.panel_header_content}>
                <div className={style.button}>
                    <div className={style.coins_block}>
                        <img src={coins} alt="coins"/>
                    </div>
                    <div className={style.sum_coins}>{bankCoins}</div>
                </div>
                <div className={style.text_center}>Магазин</div>
                {/*<div className={style.right_but}>*/}
                {/*    <div className={style.right_but_body}>*/}
                {/*        <div className={style.butt_three}>*/}
                {/*            <img src={more}/>*/}
                {/*        </div>*/}
                {/*        <div className={style.separator}></div>*/}
                {/*        <div className={style.butt_close}>*/}
                {/*            <img src={close}/>*/}
                {/*        </div>*/}
                {/*    </div>*/}
                {/*</div>*/}
            </div>
        </div>

    );
}

export default HeadPanel;

const Example = () => {
    const [popout, setPopout] = React.useState(null);
    const [actionsLog, setActionsLog] = React.useState([]);


    const closePopout = () => {
        setPopout(null);
    };

    const openDeletion = () => {
        setPopout(
            <Alert
                actions={[

                    {
                        title: "Удалить",
                        autoclose: true,
                        mode: "destructive",

                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                header="Удаление документа"
                text="Вы уверены, что хотите удалить этот документ?"
            />
        );
    };

    return (

                <View activePanel="alert" popout={popout}>
                    <Panel id="alert">
                        <PanelHeader>Alert</PanelHeader>
                        <Group>

                            <CellButton onClick={openDeletion}>Удалить документ</CellButton>

                        </Group>
                    </Panel>
                </View>
         
    );
};
