import React from 'react';
import {
    Badge,
    Cell, Counter, Epic,
    Group,
    Panel,
    PanelHeader, PanelHeaderBack, Placeholder,
    SplitCol,
    SplitLayout, Tabbar, TabbarItem, usePlatform, View,
    ViewWidth,
    VKCOM,
    withAdaptivity
} from "@vkontakte/vkui";
import {
    Icon28ClipOutline,
    Icon28MessageOutline,
    Icon28NewsfeedOutline, Icon28ServicesOutline, Icon28UserCircleOutline, Icon56NewsfeedOutline,
} from "@vkontakte/icons";
import icon1 from "../Footer/img/home_outline_28.svg";
import icon2 from "../Footer/img/Icon222.svg";
import icon3 from "../Footer/img/Icon333.svg";
import Frame from "../Frame/Frame";
import TopScreen from "../TopScreen/TopScreen";

const Example = withAdaptivity(
    ({ viewWidth }) => {
        const platform = usePlatform();
        const [activeStory, setActiveStory] = React.useState("market");
        const onStoryChange = (e) => setActiveStory(e.currentTarget.dataset.story);
        const isDesktop = viewWidth >= ViewWidth.TABLET;
        const hasHeader = platform !== VKCOM;

        return (
            <SplitLayout
                header={hasHeader && <PanelHeader separator={false} />}
                style={{ justifyContent: "center" }}
            >
                {isDesktop && (
                    <SplitCol fixed width={280} maxWidth={280}>
                        {/*<Panel>*/}
                        {/*    {hasHeader && <PanelHeader />}*/}
                        {/*    <Group>*/}
                        {/*        <Cell*/}
                        {/*            disabled={activeStory === "profile"}*/}
                        {/*            style={*/}
                        {/*                activeStory === "profile"*/}
                        {/*                    ? {*/}
                        {/*                        backgroundColor:*/}
                        {/*                            "var(--vkui--color_background_secondary)",*/}
                        {/*                        borderRadius: 8,*/}
                        {/*                    }*/}
                        {/*                    : {}*/}
                        {/*            }*/}
                        {/*            data-story="profile"*/}
                        {/*            onClick={onStoryChange}*/}
                        {/*            before={icon3}*/}
                        {/*        >*/}
                        {/*            profile*/}
                        {/*        </Cell>*/}
                        {/*    </Group>*/}
                        {/*</Panel>*/}
                    </SplitCol>
                )}

                <SplitCol
                    animate={!isDesktop}
                    spaced={isDesktop}
                    width={isDesktop ? "560px" : "100%"}
                    maxWidth={isDesktop ? "560px" : "100%"}
                >
                    <Epic
                        activeStory={activeStory}
                        tabbar={
                            !isDesktop && (
                                <Tabbar>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "home"}
                                        data-story="home"
                                    >
                                        <img src={icon1}/>
                                    </TabbarItem>
                                    <TabbarItem
                                        onClick={onStoryChange}
                                        selected={activeStory === "mess"}
                                        data-story="mess"
                                    >
                                        <img src={icon2}/>
                                    </TabbarItem>

                                    <TabbarItem style={{padding: '4px 0'}}
                                                onClick={onStoryChange}
                                                selected={activeStory === "market"}
                                                data-story="market"
                                    >
                                        <img src={icon3}/>
                                    </TabbarItem>
                                </Tabbar>
                            )
                        }
                    >

                        <View id="home" activePanel="home">
                            <Panel id="home">
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder>
                                        <img src={icon1}/>
                                    </Placeholder>
                                </Group>
                            </Panel>
                        </View>


                        <View id="mess" activePanel="mess">
                            <Panel id="mess">
                                <Group style={{ height: "1000px" }}>
                                    <Placeholder>
                                        <img src={icon2}/>
                                    </Placeholder>
                                </Group>
                            </Panel>
                        </View>

                        <View id="market" activePanel="market">
                            <Panel id="market">
                                {/*<Group style={{ height: "1000px" }}>*/}
                                {/*    <Placeholder*/}
                                {/*        icon={<Icon28ClipOutline width={56} height={56} />}*/}
                                {/*    ></Placeholder>*/}
                                {/*</Group>*/}

                                <Placeholder>
                                    <TopScreen />
                                    <Frame />
                                </Placeholder>
                            </Panel>
                        </View>

                    </Epic>
                </SplitCol>
            </SplitLayout>
        );
    },
    {
        viewWidth: true,
    }
);
export default Example;
// <Example />;
