import React from "react";
import {Alert, CellButton, Div, Group, Panel, PanelHeader, SplitCol, SplitLayout, View} from "@vkontakte/vkui";

const Popout = () => {
    const [popout, setPopout] = React.useState(null);
    const [actionsLog, setActionsLog] = React.useState([]);

    const addActionLogItem = (value) => {
        setActionsLog([...actionsLog, value]);
    };

    const closePopout = () => {
        setPopout(null);
    };

    const openAction = () => {
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Лишить права",
                        mode: "destructive",
                        autoclose: true,
                        action: () =>
                            addActionLogItem("Право на модерацию контента убрано."),
                    },
                    {
                        title: "Отмена",
                        autoclose: true,
                        mode: "cancel",
                    },
                ]}
                actionsLayout="vertical"
                onClose={closePopout}
                header="Подтвердите действие"
                text="Вы уверены, что хотите лишить пользователя права на модерацию контента?"
            />
        );
    };

    const openDeletion = () => {
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Отмена",
                        autoclose: true,
                        mode: "cancel",
                    },
                    {
                        title: "Удалить",
                        autoclose: true,
                        mode: "destructive",
                        action: () => addActionLogItem("Документ удален."),
                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                header="Удаление документа"
                text="Лимит покупки превышен!"
            />
        );
    };

    React.useEffect(() => {
        openDeletion();
    }, []);

    return (
        <SplitLayout popout={popout}>
            <SplitCol>
                <View activePanel="alert">
                    <Panel id="alert">
                        <PanelHeader>Alert</PanelHeader>
                        <Group>
                            <CellButton onClick={openAction}>Лишить права</CellButton>
                            <CellButton onClick={openDeletion}>Удалить документ</CellButton>
                        </Group>
                    </Panel>
                </View>
            </SplitCol>
        </SplitLayout>
    );
};
export default Popout;
