import React from "react";
import style from "./ItemFrame.module.css";
import coin from "../img/coin.svg";
import {useDispatch, useSelector} from "react-redux";
import {coinReduction} from "../../../Redux/bankCoinsSlice_reducers";
import {purchaseItem} from "../../../Redux/buyItemSlice_reducers";
import {Alert, PopoutWrapper} from "@vkontakte/vkui";
import Popout from "./Popout/Popout";

function ItemFrame(elItem) {
    const dispatch = useDispatch();
    let bankCoins = useSelector(state => state.bankCoins.bankCoinNum)
    let priceItem = useSelector(state => state.item.itemList[elItem.id - 1].price)

    let flag = true;

    const style_name = {
        color: "#000000",
    }

    let body_gray = {
        opacity: "1",

    }
    if (priceItem >= 1000) {
        style_name.color = "#af0000";

    }
    if (priceItem > 1200) {
        style_name.color = "#6b6a6a";
        body_gray.opacity = "0.3"
        flag = false

    }

    const [popout, setPopout] = React.useState(null);
    const closePopout = () => {
        setPopout(null);
    };

    const openDeletions = () => {
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Закрыть",
                        autoclose: true,
                        mode: "destructive",
                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                header="Лимит покупки превышен!"
                // text="Лимит покупки превышен!"
            />
        );
    };

    React.useEffect(() => {
        clickItem();
    }, []);


    const clickItem = () => {
        if (bankCoins >= priceItem && flag) {
            dispatch(coinReduction(elItem.price));
            dispatch(purchaseItem(elItem.id - 1));
        }
    }
    return (
        <div onClick={clickItem} className={style.item_frame} style={body_gray}>
            <div className={style.img_box}>
                <img src={elItem.imgItem}/>
            </div>
            <div className={style.name_item} style={style_name}>{elItem.name}</div>
            <div className={style.price_coin}>
                <img src={coin}/>
                <div className={style.price_coin_text}>{priceItem}</div>
            </div>

            <PopoutWrapper alignY="center" alignX="center">
                Some content
            </PopoutWrapper>;
            <Popout/>
        </div>
    );

}
export default ItemFrame;
