import React from "react";
import {Alert, CellButton, Div, Group, Panel, PanelHeader, SplitCol, SplitLayout, View} from "@vkontakte/vkui";

const Popout = (props) => {
    const [popout, setPopout] = React.useState(null);
    const [actionsLog, setActionsLog] = React.useState([]);

   

    const closePopout = () => {
        setPopout(null);
    };

    const openDeletions = () => {
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Закрыть",
                        autoclose: true,
                        mode: "destructive",
                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                header="Лимит покупки превышен!"
                // text="Лимит покупки превышен!"
            />
        );
    };

    React.useEffect(() => {
        openDeletions();
    }, []);

    return (
        <SplitLayout popout={popout}>
            <SplitCol>
                <View activePanel="alert">
                    <Panel id="alert">
                        <PanelHeader>Alert</PanelHeader>
                        <Group>
                            <CellButton onClick={openDeletions}>Удалить документ</CellButton>
                        </Group>
                    </Panel>
                </View>
            </SplitCol>
        </SplitLayout>
    );
};
export default Popout;

export class openDeletion {
}

export class openDeletions {
}
