import React from "react";
import {Alert, CellButton, Div, Group, Panel, PanelHeader, SplitCol, SplitLayout, View} from "@vkontakte/vkui";

const Popout = () => {
    const [popout, setPopout] = React.useState(null);
    const [actionsLog, setActionsLog] = React.useState([]);

    const addActionLogItem = (value) => {
        setActionsLog([...actionsLog, value]);
    };

    const closePopout = () => {
        setPopout(null);
    };


    const openDeletion = () => {
        setPopout(
            <Alert
                actions={[
                    {
                        title: "Закрыть",
                        autoclose: true,
                        mode: "destructive",
                        action: () => addActionLogItem("Закрыть"),
                    },
                ]}
                actionsLayout="horizontal"
                onClose={closePopout}
                header="Лимит покупки превышен!"
                // text=""
            />
        );
    };

    React.useEffect(() => {
        openDeletion();
    }, []);

    return (
        <SplitLayout popout={popout}>
            <SplitCol>
                <View activePanel="alert">
                    <Panel id="alert">
                        <PanelHeader>Alert</PanelHeader>
                        <Group>
                            <CellButton onClick={openAction}>Лишить права</CellButton>
                            <CellButton onClick={openDeletion}>Удалить документ</CellButton>
                            {actionsLog.map((value, i) => (
                                <Div key={i}>{value}</Div>
                            ))}
                        </Group>
                    </Panel>
                </View>
            </SplitCol>
        </SplitLayout>
    );
};
export default Popout;
